            ,cipsrv_nhdplus_h.snap_to_common_grid(patchgeo,int_srid::VARCHAR,0.001)
         )
         INTO step1geo
         FROM
         cipsrv_nhdplus_h.nhdpluscatchment a
         WHERE
         a.nhdplusid = r.expand;
      
      EXCEPTION
         WHEN OTHERS THEN
            RAISE WARNING '% % % %',r.expand,r.static,ST_ASEWKT(r.patch),r.size;
            RAISE;
      
      END;
      
