      THEN
         RAISE EXCEPTION '% % % % %',r.expand,r.static,ST_ASEWKT(r.patch),r.size,ST_ASEWKT(step1geo);
         
      END IF;
      
      BEGIN
         SELECT
         ST_DIFFERENCE(
             step1geo
            ,staticgeo
         )
         INTO step2geo;
      
      EXCEPTION
         WHEN OTHERS THEN
            RAISE WARNING '% % % %',r.expand,r.static,ST_ASEWKT(r.patch),r.size;
            RAISE;
      
